import { ChatManager, TokenProvider } from "@pusher/chatkit-client";

function handleInput(event) {
  const { value, name } = event.target;

  this.setState({
    [name]: value
  });
}

function connectToChatkit() {
  const { userId } = this.state;

  const tokenProvider = new TokenProvider({
    url:
      "https://us1.pusherplatform.io/services/chatkit_token_provider/v1/0c668823-ec59-4ebd-a1cf-a5932d8d7070/token"
  });

  const chatManager = new ChatManager({
    instanceLocator: "v1:us1:0c668823-ec59-4ebd-a1cf-a5932d8d7070",
    userId,
    tokenProvider
  });

  return chatManager
    .connect()
    .then(currentUser => {
      this.setState(
        {
          currentUser,
          rooms: currentUser.rooms
        },
        () => connectToRoom.call(this)
      );
    })
    .catch(console.error);
}

function connectToRoom() {
  const { currentUser } = this.state;
  const roomId = "21430947"
  this.setState({
    messages: []
  });

  return currentUser
    .subscribeToRoomMultipart({
      roomId,
      messageLimit: 10,
      hooks: {
        onMessage: message => {
          const m = this.state.messages;
          this.setState({
            messages: [...m, message],
          });
        },
        onPresenceChanged: () => {
          const { currentRoom } = this.state;
          this.setState({
            roomUsers: currentRoom.users.sort(a => {
              if (a.presence.state === "online") return -1;

              return 1;
            })
          });
        }
      }
    })
    .then(currentRoom => {
      this.setState({
        currentRoom,
        rooms: currentUser.rooms,
      });
    })
    .catch(console.error);
}

function sendMessage() {
  const { newMessage, currentUser, currentRoom } = this.state;
  const parts = [];

  if (newMessage.trim() === "") return;

  parts.push({
    type: "text/plain",
    content: newMessage
  });

  currentUser.sendMultipartMessage({
    roomId: `${currentRoom.id}`,
    parts
  });

  this.setState({
    newMessage: "",
  });
}

export {
  handleInput,
  connectToRoom,
  connectToChatkit,
  sendMessage,
}
